package code20to29;

public class Code27_28 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b, c;
		a=10;
		System.out.println(a);
		System.out.println(b=a++);//b는 10이 나온 뒤 a에 1이 증가된다.
		System.out.println(a);//a=11이 된다.
		System.out.println(c=++a);//a에서 1이 증가된 상태로 출력된다.
		
	}

}
