public class Code40_41 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a=100000.123;
		System.out.println(a);
		System.out.println((int)a); //int로 바꾸면 소숫점이 날라감.
		System.out.println((byte)a);// byte로 바꿀 시 값이 완전히 달라짐
//캐스터 연산자를 이용하여 형변환을 할경우 데이터의 손실이 생길 수 있음
		
	}

}
