package section4;

public class Code43 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10, b=5, c=20;
		boolean w,x,y,z;
		w=a>b&&c>b;
		x=!(a>b&&c>b);
		y=c<a||b<a;
		z=c<=a||b<=a;
		System.out.println("w="+w);
		System.out.println("x="+x);
		System.out.println("y="+y);
		System.out.println("z="+z);
	}

}
